import { useEffect, useState } from "react";
import axios from 'axios';
import PokemonCard from "../../components/pokemonCard/Index";

const TeamBuilder = () => {

    const lastPoke = 200;
    const [pokemonList, setPokemonList] = useState([]);

    const getPokemons = async () => {
        var pokemons = [];
        for (let i = 1; i <= lastPoke; i++) {
            pokemons.push(`https://pokeapi.co/api/v2/pokemon/${i}/`);
        }
        axios.all(pokemons.map((pokemon) => axios.get(pokemon))).then((res) => setPokemonList(res.data));
    };

    useEffect(() => {
        getPokemons();
    }, [])

    return (
        <>
            {pokemonList.length > 0 ?
                (<div>
                    < PokemonCard pokemonName={pokemonList[1].data.name} pokemonType1={pokemonList[1].data.types.type.name} pokemonImage={pokemonList[1].data.sprites.official_artwork.front_default} />
                </div >)
                :
                <       h1>Hello, World!</h1>
            }
        </>
    )


}

export default TeamBuilder;
