import { useEffect, useState } from "react";
import axios from 'axios';
import PokemonCard from "../../components/pokemonCard/Index";

const TeamBuilder = () => {

    const lastPoke = 905;
    const [pokemonList, setPokemonList] = useState([]);

    const getPokemons = () => {
        var pokemons = [];
        for (let i = 1; i <= lastPoke; i++) {
            pokemons.push(`https://pokeapi.co/api/v2/pokemon/${i}/`);
        }
        axios.all(pokemons.map((pokemon) => axios.get(pokemon))).then((res) => setPokemonList(res));
    };

    useEffect(() => {
        getPokemons();
    }, [])

    return (
        pokemonList.length > 0 ?
            <div>
                < PokemonCard pokemonName={pokemonList[100].name} pokemonType1={pokemonList[100].types.type.name} pokemonImage={pokemonList[100].sprites.official_artwork.front_default} />
            </div >
            :
            <       h1>Hello, World!</h1>
    )


}

export default TeamBuilder;
