import { useEffect, useState } from "react";
import axios from 'axios';
import PokemonCard from "../../components/pokemonCard/Index";
import { Container, Square, texter } from './style';
import AddPokemonField from "../../components/addPokemonField";

const TeamBuilder = () => {

    const lastPoke = 300;
    const [pokemonList, setPokemonList] = useState([]);
    const [starterPokemonName, setStarterPokemonName] = useState();

    const getPokemons = async () => {
        var pokemons = [];
        for (let i = 1; i <= lastPoke; i++) {
            pokemons.push(`https://pokeapi.co/api/v2/pokemon/${i}/`);
        }
        await axios.all(pokemons.map((pokemon) => axios.get(pokemon))).then((res) => setPokemonList(res)).then(
            console.log(pokemonList)
        );
    };

    useEffect(() => {
        getPokemons();
    }, [])

    return (
        <Container>
            <h1>Select Starter Pokemon</h1>
            <AddPokemonField setPokemonName={setStarterPokemonName} pokemonName={starterPokemonName}></AddPokemonField>
            <Square></Square>
        </Container>
    )

}

export default TeamBuilder;
